if ([3,4,5,6,7].includes(scenario.age.index) && scenario.gender.index !== 0) {
    scenario.risk_factor_list.splice(3, 0, 'Mang thai')
}

scenario.risk_factor_list = [
  '慢性肺病，中度至重度哮喘或吸烟',
  '严重心脏病',
  '免疫系统减弱(接受过癌症治疗、长期使用激素、进行过移植手术或HIV/AIDS)',
