if (scenario.state_links[scenario.user_state.state].url) {

    let selected_link = scenario.state_links[scenario.user_state.state].link;
    let selected_url = scenario.state_links[scenario.user_state.state].url;

    if (scenario.state_links[scenario.user_state.state].redirect_patients) {
        scenario.redirect = true;
        scenario.state_msg = "Thanks! Your location has its own self-assessment tool. Please [click here](" + selected_url + ") to be directed to it.";     
    } else {
        scenario.state_msg = "\n\nPlease also see your local area’s website:\n[" + selected_link + "](" + selected_url + ")";
    }

}

steps.args:
{
    custom_outcome_message: scenario.state_msg,
    show_core_message: true,
    locale: scenario.locale,
    user_state: scenario.user_state
}

